import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Box, Button, FormControl, FormLabel, Input } from "@chakra-ui/react";
import NavBar from "../components/Navbar";
import Footer from "../components/Footer";

const EditStudent = () => {

    const { id } = useParams();
    const navigate = useNavigate();
    const [studentData, setStudentData] = useState(null);

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await fetch(`http://localhost:3001/student/${id}`);
                const data = await response.json();
                setStudentData(data);
            } catch (err) {
                console.log(err);
            }
        };

        fetchStudent();

    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:3001/student/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(studentData),
            });
            const data = await response.json();

            navigate("/student");

        } catch (err) {
            console.log(err);
        }
    };

    const handleChange = (e) => {

        const { name, value } = e.target;
        let faculty = ""; 

        if (name === "programStudy") {
            switch (value) {
                case "Ekonomi":
                case "Manajemen":
                case "Akuntansi":
                    faculty = "Fakultas Ekonomi";
                    break;
                case "Administrasi Publik":
                case "Administrasi Bisnis":
                case "Hubungan Internasional":
                    faculty = "Fakultas Ilmu Sosial dan Politik";
                    break;
                case "Teknik Sipil":
                case "Arsitektur":
                    faculty = "Fakultas Teknik";
                    break;
                case "Matematika":
                case "Fisika":
                case "Informatika":
                    faculty = "Fakultas Teknologi Informasi dan Sains";
                    break;
                default:
                    faculty = "";
                    break;
            }
        }

        setStudentData((prevStudent) => ({ ...prevStudent, [name]: value, faculty }));
    };

    return (
        <>
            <NavBar/>

            {!studentData ? (
                <p>Loading ...</p>
            ) : (

                <form id="form-student" onSubmit={handleSubmit}>
                    <img src={studentData.profilePicture} alt={studentData.fullname + "Picture"}></img>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-name">Fullname</FormLabel>
                            <Input
                                id="input-name" 
                                name="fullname"
                                type="text"
                                value={studentData.fullname}
                                onChange={handleChange}
                                data-testid="name"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-address">Address</FormLabel>
                            <Input
                                id="input-address" 
                                name="address"
                                type="text"
                                value={studentData.address}
                                onChange={handleChange}
                                data-testid="address"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-phoneNumber">Phone Number</FormLabel>
                            <Input
                                id="input-phoneNumber" 
                                name="phoneNumber"
                                type="text"
                                value={studentData.phoneNumber}
                                onChange={handleChange}
                                data-testid="phoneNumber"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-date">Birth Date</FormLabel>
                            <Input
                                id="input-date"
                                name="birthDate"
                                type="text"
                                value={studentData.birthDate}
                                onChange={handleChange}
                                data-testid="date"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-gender">Gender</FormLabel>
                            <Input
                                id="input-gender"
                                name="gender"
                                type="text"
                                value={studentData.gender}
                                onChange={handleChange}
                                data-testid="gender"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <FormControl>
                            <FormLabel htmlFor="input-prody">Program Study</FormLabel>
                            <Input
                                id="input-prody"
                                name="programStudy"
                                type="text"
                                value={studentData.programStudy}
                                onChange={handleChange}
                                data-testid="prody"
                            />
                        </FormControl>
                    </Box>
                    <Box>
                        <Button colorScheme="blue" type="submit" data-testid="edit-btn">
                            Simpan
                        </Button>
                    </Box>
                </form>
            )}
        </>
    );
};

export default EditStudent;
