import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import NavBar from "../components/Navbar";

const EditStudent = () => {

    const { id } = useParams();
    const navigate = useNavigate();
    const [studentData, setStudentData] = useState(null);

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await fetch(`http://localhost:3001/student/${id}`);
                const data = await response.json();
                setStudentData(data);
            } catch (err) {
                console.log(err);
            }
        };

        fetchStudent();

    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:3001/student/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(studentData),
            });
            const data = await response.json();

            navigate("/student");

        } catch (err) {
            console.log(err);
        }
    };

    const handleChange = (e) => {

        const { name, value } = e.target;
        let faculty = ""; 

        if (name === "programStudy") {
            switch (value) {
                case "Ekonomi":
                case "Manajemen":
                case "Akuntansi":
                    faculty = "Fakultas Ekonomi";
                    break;
                case "Administrasi Publik":
                case "Administrasi Bisnis":
                case "Hubungan Internasional":
                    faculty = "Fakultas Ilmu Sosial dan Politik";
                    break;
                case "Teknik Sipil":
                case "Arsitektur":
                    faculty = "Fakultas Teknik";
                    break;
                case "Matematika":
                case "Fisika":
                case "Informatika":
                    faculty = "Fakultas Teknologi Informasi dan Sains";
                    break;
                default:
                    faculty = "";
                    break;
            }
        }

        setStudentData((prevStudent) => ({ ...prevStudent, [name]: value, faculty }));
    };

    return (
        <>
            <NavBar/>

            {!studentData ? (
                <p>Loading ...</p>
            ) : (

                <form id="form-student"  onSubmit={handleSubmit}>
                    <img src={studentData.profilePicture} alt={studentData.fullname + "Picture"}></img>
                    <div>
                        <label htmlFor="input-name">Fullname</label>
                        <input
                            id="input-name" 
                            name="fullname"
                            type="text"
                            value={studentData.fullname}
                            onChange={handleChange}
                            data-testid="name"
                        />
                        <br/><br/>
                    </div>
                    <div>
                        <label htmlFor="input-address">Address</label>
                        <input
                            id="input-address" 
                            name="address"
                            type="text"
                            value={studentData.address}
                            onChange={handleChange}
                            data-testid="address"
                        />
                        <br/><br/>
                    </div>
                    <div>
                        <label htmlFor="input-phoneNumber">Phone Number</label>
                        <input
                            id="input-phoneNumber" 
                            name="phoneNumber"
                            type="text"
                            value={studentData.phoneNumber}
                            onChange={handleChange}
                            data-testid="phoneNumber"
                        />
                        <br/><br/>
                    </div>
                    <div>
                        <label htmlFor="input-date">Birth Date</label>
                        <input
                            id="input-date"
                            name="birthDate"
                            type="text"
                            value={studentData.birthDate}
                            onChange={handleChange}
                            data-testid="date"
                        />
                        <br/><br/>
                    </div>
                    <div>
                        <label htmlFor="input-gender">Gender</label>
                        <input
                            id="input-gender"
                            name="gender"
                            type="text"
                            value={studentData.gender}
                            onChange={handleChange}
                            data-testid="gender"
                        />
                        <br/><br/>
                    </div>
                    <div>
                        <label htmlFor="input-prody">Program Study</label>
                        <select
                            id="input-prody"
                            name="programStudy"
                            value={studentData.programStudy}
                            onChange={handleChange}
                            data-testid="prody"
                        >
                            <option value="">Select Program Study</option>
                            <option value="Ekonomi">Ekonomi</option>
                            <option value="Manajemen">Manajemen</option>
                            <option value="Akuntansi">Akuntansi</option>
                            <option value="Administrasi Publik">Administrasi Publik</option>
                            <option value="Administrasi Bisnis">Administrasi Bisnis</option>
                            <option value="Hubungan Internasional">Hubungan Internasional</option>
                            <option value="Teknik Sipil">Teknik Sipil</option>
                            <option value="Arsitektur">Arsitektur</option>
                            <option value="Matematika">Matematika</option>
                            <option value="Fisika">Fisika</option>
                            <option value="Informatika">Informatika</option>
                        </select>
                        <br/><br/>
                    </div>
                    <div>
                        <button id="add-btn" type="submit" data-testid="edit-btn">
                            Simpan
                        </button>
                    </div>
                </form>
            )}
        </>
    );
};

export default EditStudent;
